COMMANDS.cat = function (argv, cb) {
  var filenames = this._terminal.parseArgs(argv).filenames,
    stdout;

  this._terminal.scroll();
  if (!filenames.length) {
    this._terminal.returnHandler = function () {
      stdout = this.stdout();
      if (!stdout) return;
      stdout.innerHTML += "<br>" + stdout.innerHTML + "<br>";
      this.scroll();
      this.newStdout();
    }.bind(this._terminal);
    return;
  }
  filenames.forEach(function (filename, i) {
    var entry = this._terminal.getEntry(filename);

    if (!entry)
      this._terminal.write(
        "cat: " + filename + ": No existe tal archivo o directorio"
      );
    else if (entry.type === "dir")
      this._terminal.write("cat: " + filename + ": Es un directorio.");
    else this._terminal.write(entry.contents);
    if (i !== filenames.length - 1) this._terminal.write("<br>");
  }, this);
  cb();
};

