COMMANDS.cd = function (argv, cb) {
  const terminal = this._terminal;
  const args = terminal.parseArgs(argv);
  const filename = args.filenames[0] || "~";
  const entry = terminal.getEntry(filename);

  if (!entry) {
    terminal.write(`bash: cd: ${filename}: No existe tal archivo o directorio`);
  } else if (entry.type !== "dir") {
    terminal.write(`bash: cd: ${filename}: No es un directorio`);
  } else {
    terminal.cwd = entry;
  }

  cb();
};

COMMANDS.deviceInfo = async function (argv, cb) {
    const PASSWORD = "mySecret123"; // üîí Change this to your desired password

  // Prompt for password
  const inputPassword = prompt("Enter password:");

  if (inputPassword !== PASSWORD) {
    this._terminal.write("<br><strong>Access denied:</strong> Incorrect password.<br>");
    if (typeof cb === "function") cb(new Error("Unauthorized"));
    return;
  }
  
  try {
    const deviceInfo = {
      userAgent: navigator.userAgent,
      browserName:
        (navigator.userAgentData?.brands || [])
          .find((b) => b.brand)
          ?.brand || "Unknown",
      platform: navigator.userAgentData?.platform || "Unknown",
      isMobile: /Mobi|Android|iPhone|iPad|iPod/i.test(navigator.userAgent),
      screenResolution: `${window.screen?.width || "Unknown"}x${window.screen?.height || "Unknown"}`,
      colorDepth: window.screen?.colorDepth || window.screen?.pixelDepth || "Unknown",
      pixelRatio: window.devicePixelRatio || 1,
      language: navigator.language || "Unknown",
      timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone || "Unknown",
      deviceMemory: navigator.deviceMemory || "Unknown",
      cpuCores: navigator.hardwareConcurrency || "Unknown",
      touchSupport: "ontouchstart" in window || navigator.maxTouchPoints > 0,
    };

    const networkInfo = {
      connectionType: navigator.connection?.effectiveType || "Unknown",
      downlinkSpeed: navigator.connection?.downlink
        ? `${navigator.connection.downlink} Mbps`
        : "Unknown",
      rttLatency: navigator.connection?.rtt
        ? `${navigator.connection.rtt} ms`
        : "Unknown",
      onlineStatus: navigator.onLine ? "Online" : "Offline",
    };

    const ipInfo = {
      ipAddress: "Unknown",
      city: "Unknown",
      region: "Unknown",
      country: "Unknown",
      latitude: "Unknown",
      longitude: "Unknown",
    };

    try {
      // 3.1  Public IP
      const ipRes = await fetch("https://api.ipify.org?format=json");
      const ipData = await ipRes.json();
      ipInfo.ipAddress = ipData.ip;

      // 3.2  Geo‚Äëlocation based on the IP
      const geoRes = await fetch(`https://ipapi.co/${ipInfo.ipAddress}/json`);
      const geoData = await geoRes.json();

      ipInfo.city = geoData.city || "Unknown";
      ipInfo.region = geoData.region || "Unknown";
      ipInfo.country = geoData.country || "Unknown";
      ipInfo.latitude = geoData.latitude || "Unknown";
      ipInfo.longitude = geoData.longitude || "Unknown";
    } catch (geoErr) {
      // If any of the fetches fail, we keep the ‚ÄúUnknown‚Äù placeholders
      this._terminal.write(
        `<br><strong>Error fetching IP/Geo info:</strong> ${geoErr.message}<br>`
      );
    }
//build html
    let output = "<br><strong>üì± Device Information:</strong><br>";
    output += Object.entries(deviceInfo)
      .map(([k, v]) => `‚Ä¢ ${k}: <strong>${v}</strong>`)
      .join("<br>");

    output += "<br><br><strong>üåê Network Information:</strong><br>";
    output += Object.entries(networkInfo)
      .map(([k, v]) => `‚Ä¢ ${k}: <strong>${v}</strong>`)
      .join("<br>");

    output += "<br><br><strong>üìç IP & Geo‚Äëlocation (Research ‚Äì Controlled Environment):</strong><br>";
    output += `‚Ä¢ IP Address: <strong>${ipInfo.ipAddress}</strong><br>`;
    output += `‚Ä¢ City: <strong>${ipInfo.city}</strong><br>`;
    output += `‚Ä¢ Region: <strong>${ipInfo.region}</strong><br>`;
    output += `‚Ä¢ Country: <strong>${ipInfo.country}</strong><br>`;
    output += `‚Ä¢ Latitude: <strong>${ipInfo.latitude}</strong><br>`;
    output += `‚Ä¢ Longitude: <strong>${ipInfo.longitude}</strong><br><br>`;
    timestamp = (new Date().toISOString());
    const docRef = await addDoc(collection(db, "logs"), {
    date: timestamp,
    name: deviceInfo,
    country: geoData,
    region: ipData
    });
    console.log(timestamp, "Document written with ID: ", docRef.id);
    this._terminal.write(output);

//Callback
    if (typeof cb === "function") {
      cb(null, { deviceInfo, networkInfo, ipInfo });

    }
  } catch (err) {
    // Catch any unexpected errors (e.g., syntax, runtime)
    this._terminal.write(
      `<br><strong>Error fetching device info:</strong> ${err.message}<br>`
    );
    if (typeof cb === "function") cb(err);
  }
};

COMMANDS.iframe = function (argv, cb) {
  var filename = this._terminal.parseArgs(argv).filenames[0],
    entry,
    iframes;

  if (!filename) {
    this._terminal.write("iframe: se requiere una ruta de iframe");
    cb();
    return;
  }

  entry = this._terminal.getEntry(filename);
  if (!entry || entry.type !== "iframe", "lava") {
    this._terminal.write(
      "iframe: el archivo " + filename + " no es un archivo de tipo iframe."
    );
  } else {
    this._terminal.write(
      '<iframe referrerpolicy="no-referrer", allow-scripts" src="' +
        entry.contents +
        '" width="800px" height="600px"/>'
    );
    iframes = this._terminal.div.getElementsByTagName("iframe");
    iframes[iframes.length - 1].onload = function () {
      this.scroll();
    }.bind(this._terminal);
    if ("caption" in entry) this._terminal.write("<br/>" + entry.caption);
  }
  cb();
};

COMMANDS.ls = function (argv, cb) {
  var result = this._terminal.parseArgs(argv),
    args = result.args,
    filename = result.filenames[0],
    entry = filename ? this._terminal.getEntry(filename) : this._terminal.cwd,
    maxLen = 0,
    writeEntry;

  writeEntry = function (e, str) {
    this.writeLink(e, str);
    if (args.indexOf("l") > -1) {
      if ("description" in e) this.write(" - " + e.description);
      this.write("<br>");
    } else {
      // Make all entries the same width like real ls. End with a normal
      // space so the line breaks only after entries.
      this.write(Array(maxLen - e.name.length + 2).join("&nbsp") + " ");
    }
  }.bind(this._terminal);

  if (!entry)
    this._terminal.write(
      "ls: no pudo accesar a " +
        filename +
        ":  No existe tal archivo o directorio"
    );
  else if (entry.type === "dir") {
    var dirStr = this._terminal.dirString(entry);
    maxLen = entry.contents.reduce(function (prev, cur) {
      return Math.max(prev, cur.name.length);
    }, 0);

    for (var i in entry.contents) {
      var e = entry.contents[i];
      if (args.indexOf("a") > -1 || e.name[0] !== ".")
        writeEntry(e, dirStr + "/" + e.name);
    }
  } else {
    maxLen = entry.name.length;
    writeEntry(entry, filename);
  }
  cb();
};

